/**
 * @format
 * @flow strict-local
 */

import AsyncStorage from '@react-native-async-storage/async-storage';
import {observer} from 'mobx-react';
import React, {useEffect, useRef, useState} from 'react';
import {useStore} from '../../constants/useStore';
import {useNavigation} from '@react-navigation/native';
import {
  DeviceEventEmitter,
  FlatList,
  Image,
  Keyboard,
  NativeEventEmitter,
  NativeModules,
  PermissionsAndroid,
  Platform,
  Pressable,
  StyleSheet,
  TextInput,
  View,
  useColorScheme,
} from 'react-native';
import {DHeight, DWidth, backgroundColor} from '../../constants/Constants';
import Header from '../../components/Header';
import {PlayerRow} from './PlayerRow';
import Icon from 'react-native-vector-icons/FontAwesome';
import {BottomSheetModal, BottomSheetTextInput} from '@gorhom/bottom-sheet';
import {Modal} from '../../components/Modal';
import Text from '../../components/Text';
import {launchCamera, launchImageLibrary} from 'react-native-image-picker';
import {colors, spacing} from '../../theme';
import {translate, translateWithOptions} from '../../i18n';
import * as storage from '../../utils/storage';
import I18n from 'i18n-js';
import {showToast} from '../../utils/snackbar';

const {CameraModule} = NativeModules;
const cameraEventEmitter = new NativeEventEmitter();

// import player from '../../assets/images/player2.png';
const AllPlayers = observer(() => {
  const player = require('../../assets/images/player2.png');

  const [players, setPlayers] = useState([]);
  const {
    langStore: {language},
  } = useStore();
  const nav = useNavigation();
  const addRef = useRef<BottomSheetModal>(null);
  const cameraModal = useRef<BottomSheetModal>(null);
  const colorScheme = useColorScheme() === 'dark';

  const [userPicture, setUserPicture] = useState('');
  const [playerName, setPlayerName] = useState('');
  const getPlayers = async () => {
    return await storage.load('players');
  };
  useEffect(() => {
    getPlayers().then(myPlayer => {
      if (myPlayer) {
        setPlayers(myPlayer);
      }
    });
  }, []);

  const addPicture = () => {
    Keyboard.dismiss();
    cameraModal?.current?.present();
  };
  const takePhoto = () => {
    cameraModal.current.close();
    if (Platform.OS === 'android') {
      CameraModule.startCamera();

      const imageCapturedListener = cameraEventEmitter.addListener(
        'imageCaptured',
        imageUri => {
          if (imageUri !== null) {
            setUserPicture(imageUri);
            imageCapturedListener.remove();
          }
        },
      );
    } else {
      launchCamera(
        {
          cameraType: 'back',
          mediaType: 'photo',
          maxHeight: 200,
          maxWidth: 200,
          quality: 0.3,
          includeBase64: true,
        },

        response => {
          if (response) {
            if (!response.didCancel) {
              if (response?.assets[0]) {
                const file = {
                  file: `data:image/jpeg;base64,${response.assets[0].base64}`,
                };
                // this.changeAvatar(this.props.user.id, response.data).then(() =>
                // {
                // 	this.props.fetchUser();
                // });
              }
            }
          }
        },
      );
    }
  };

  const selectFromDoc = () => {
    cameraModal?.current?.close();
    Keyboard.dismiss();
    if (Platform.OS === 'android') {
      PermissionsAndroid.request(
        PermissionsAndroid.PERMISSIONS.READ_EXTERNAL_STORAGE,
      ).then(() => {
        launchImageLibrary(
          {
            title: 'انتخاب از گالری',
            // maxWidth: 600,
            // maxHeight: 600,
            quality: 0.7,
            mediaType: 'photo',
            includeBase64: true,
            // eslint-disable-next-line
          },
          response => {
            if (response) {
              if (!response.didCancel) {
                if (response?.assets[0]) {
                  const file = `data:image/jpeg;base64,${response.assets[0].base64}`;

                  setUserPicture(file);

                  // this.changeAvatar(this.props.user.id, response.data).then(() =>
                  // {
                  // 	this.props.fetchUser();
                  // });
                }
              }
            }
          },
        );
      });
    } else {
      launchCamera(
        {
          title: 'دوربین',
          // maxWidth: 600,
          // maxHeight: 600,
          quality: 0.7,
          mediaType: 'photo',
          includeBase64: true,
          // eslint-disable-next-line
        },
        response => {
          if (response) {
            if (!response.didCancel) {
              if (response?.assets[0]) {
                const file = `data:image/jpeg;base64,${response.assets[0].base64}`;

                setUserPicture(file);

                // this.changeAvatar(this.props.user.id, response.data).then(
                //   () => {
                //     this.props.fetchUser();
                //   },
                // );
              }
            }
          }
        },
      );
    }
  };

  const addPlayer = () => {
    if (playerName) {
      addRef?.current?.close();
      const arr = [...players];
      arr.push({id: Date.now(), name: playerName, avatar: userPicture});
      storage.save('players', arr);
      setPlayers(arr);
      showToast({
        text: translateWithOptions('players.addPlayerSucceed', {
          player: playerName,
        }),
      });
      setPlayerName('');
      setUserPicture('');
    } else {
      showToast({
        text: translate('players.shouldWritePlayerName'),
        mode: 'danger',
      });
    }
  };

  const removeItem = id => {
    const arr = [...players];
    const filteredArr = arr.filter(item => item.id !== id);
    AsyncStorage.setItem('players', JSON.stringify(filteredArr));
    setPlayers(filteredArr);
  };
  return (
    <View
      style={{
        width: '100%',
        paddingTop: 20,
        height: '100%',
        backgroundColor: colors.background,
      }}>
      <Header title={translate('game.myPlayers')} />
      {players ? (
        <FlatList
          data={players}
          keyExtractor={item => item.id}
          style={{flex: 0.7, maxHeight: DHeight * 0.8}}
          contentContainerStyle={{
            marginTop: 20,
            paddingBottom: 50,
            // marginBottom: 40,
          }}
          ListEmptyComponent={() => {
            return (
              <View style={styles.emptyList}>
                <Image
                  source={require('../../assets/images/empty1.png')}
                  style={{width: 100, height: 150}}
                />
                <Text style={{fontSize: 20}}>
                  {translate('game.anyPlayerExist')}
                </Text>
              </View>
            );
          }}
          renderItem={({item}) => {
            return (
              <PlayerRow
                item={item}
                key={item?.id}
                removeItem={() => removeItem(item.id)}
              />
            );
          }}
        />
      ) : null}
      <View
        style={[
          styles.addBtn,
          {
            backgroundColor: !colorScheme
              ? colors.modalBackground
              : colors.bottomCenterColor,
          },
        ]}>
        <Pressable
          onPress={() => {
            addRef?.current?.present();
          }}
          style={styles.addBtnIcon}>
          <Icon name="user-plus" size={20} color={colors.text} />
        </Pressable>
      </View>
      <Modal
        modalRef={addRef}
        index={0}
        onDismiss={() => {
          console.log('dismiss called');
          addRef.current.close();
        }}
        onBackdropPress={() => {
          console.log('backdrop pressed ');
          addRef.current.close();
        }}
        snapPoints={['70%']}
        backgroundStyle={{
          backgroundColor: colors.modalBackground,
        }}
        onChange={e => {}}>
        <View
          style={{
            height: '100%',
            width: '100%',
            justifyContent: 'space-around',
            alignItems: 'center',
          }}>
          <Pressable
            onPress={() => {
              addPicture();
            }}
            hitSlop={10}>
            <View style={styles.userphoto}>
              <Image
                source={userPicture ? {uri: userPicture} : player}
                style={{width: '100%', height: '100%'}}
              />
            </View>
            <Icon
              name="camera"
              size={25}
              color={'white'}
              style={{
                position: 'absolute',
                bottom: -10,
                right: -10,
                backgroundColor: '#2b60b5',
                padding: 5,
                borderRadius: 12.5,
                overflow: 'hidden',
                // borderWidth: 1,
              }}
            />
          </Pressable>
          <BottomSheetTextInput
            value={playerName}
            onChangeText={text => {
              setPlayerName(text);
            }}
            // autoFocus={true}
            style={[
              styles.modalInput,
              {
                backgroundColor: colors.background,
                color: colors.text,
                fontSize: language === 'fa' ? spacing.lg : 16,
                fontFamily:
                  language === 'fa' ? 'Digi Nofar Bold' : 'Wizard World',
              },
            ]}
            selectionColor={colors.text}
            placeholder={translate('game.playerName')}
            placeholderTextColor={colors.textDim}
          />
          <Pressable
            onPress={() => {
              addPlayer();
            }}
            style={[
              styles.modalBtn,
              {
                backgroundColor: colors.background,
              },
            ]}>
            <Text>{translate('common.addBtn')}</Text>
          </Pressable>
        </View>
      </Modal>
      <Modal
        modalRef={cameraModal}
        index={0}
        onDismiss={() => cameraModal?.current?.close()}
        snapPoints={['50%']}
        vertical={30}
        backgroundStyle={{
          backgroundColor: colors.background,
        }}>
        <View style={styles.selectImageContainer}>
          <Pressable onPress={takePhoto} style={styles.addImageBtnCard}>
            <View style={styles.addPhotoCard}>
              <Icon name="camera" size={50} color={'white'} />
            </View>
            <Text style={{color: 'white'}}>{translate('common.camera')}</Text>
          </Pressable>
          <Pressable onPress={selectFromDoc} style={styles.addImageBtnCard}>
            <View style={styles.addPhotoCard}>
              <Icon name="image" size={50} color={'white'} />
            </View>
            <Text style={{color: 'white'}}>{translate('common.gallery')}</Text>
          </Pressable>
        </View>
      </Modal>
    </View>
  );
});

